import "./App.css";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import Login from "./Components/Login";
import { useState, useEffect } from "react";
import { addUser, getUsers } from "./Services/UsersService";
import { getAdmins } from "./Services/AdminsService";
import { getDeals } from "./Services/DealsService";
import Admin from "./Admin";
import User from "./User";
import Register from "./Components/Register";

function App() {
  const [logged, setLogged] = useState(false);
  const [users, setUsers] = useState([]);
  const [admins, setAdmins] = useState([]);
  const [deals, setDeals] = useState([]);

  useEffect(() => {
    getUsers().then((res) => {
      setUsers(res.data);
    });

    getAdmins().then((res) => {
      setAdmins(res.data);
    });

    getDeals().then((res) => {
      setDeals(res.data);
    });
  }, []);

  const handleLogged = () => {
    setLogged(!logged);
  };

  const handleRegister = (newUser) => {
    addUser(newUser);
    setUsers([...users, newUser]);
  };

  const handleDeclineDeal = () => {};

  const handleApproveDeal = (id) => {
    setDeals(
      deals.map((deal) =>
        deal.id === id ? { ...deal, confirmed: True } : deal
      )
    );
  };

  return (
    <div>
      <Router>
        <div className="container">
          <Routes>
            <Route
              exact
              path="/"
              element={
                <Login
                  users={users}
                  admins={admins}
                  handleLogged={handleLogged}
                />
              }
            />
            {logged && (
              <Route
                exact
                path="/admin"
                element={
                  <Admin
                    handleLogged={handleLogged}
                    deals={deals}
                    handleDeclineDeal={handleDeclineDeal}
                    handleApproveDeal={handleApproveDeal}
                  />
                }
              />
            )}
            {logged && (
              <Route
                exact
                path="/user"
                element={<User handleLogged={handleLogged} />}
              />
            )}
            <Route
              exact
              path="/register"
              element={
                <Register
                  users={users}
                  admins={admins}
                  handleRegister={handleRegister}
                />
              }
            />
          </Routes>
        </div>
      </Router>
    </div>
  );
}

export default App;
