import "./App.css";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import Login from "./Components/Login";
import { useState, useEffect } from "react";
import { addUser, getUsers } from "./Services/UsersService";
import { getAdmins } from "./Services/AdminsService";
import { getDeals, addDeal } from "./Services/DealsService";
import Admin from "./Admin";
import User from "./User";
import Register from "./Components/Register";
import Client from "./Client";
import SellHistory from "./Components/SellHistory";
import BuyHistory from "./Components/BuyHistory";

function App() {
  const [logged, setLogged] = useState(false);
  const [users, setUsers] = useState([]);
  const [admins, setAdmins] = useState([]);
  const [deals, setDeals] = useState([]);
  const [actualUser, setActualUser] = useState(0);

  useEffect(() => {
    getUsers().then((res) => {
      setUsers(res.data);
    });

    getAdmins().then((res) => {
      setAdmins(res.data);
    });

    getDeals().then((res) => {
      setDeals(res.data);
    });
  }, []);

  const handleLogged = (id) => {
    setActualUser(id);
    setLogged(!logged);
  };

  const handleRegister = (newUser) => {
    addUser(newUser);
    setUsers([...users, newUser]);
  };

  const handleDeclineDeal = (id) => {};

  const handleApproveDeal = (id) => {
    setDeals(
      deals.map(
        (deal) => console.log(deal)
        //deal.id === id ? { ...deal, posted: 1 } : console.log(deal.id)
      )
    );
  };

  return (
    <div>
      <Router>
        <div className="container">
          <Routes>
            <Route
              exact
              path="/"
              element={
                <Login
                  users={users}
                  admins={admins}
                  handleLogged={handleLogged}
                />
              }
            />
            {logged && (
              <Route
                exact
                path="/admin"
                element={
                  <Admin
                    handleLogged={handleLogged}
                    deals={deals}
                    handleDeclineDeal={handleDeclineDeal}
                    handleApproveDeal={handleApproveDeal}
                  />
                }
              />
            )}
            {logged && (
              <Route
                exact
                path="/seller"
                element={
                  <User
                    handleLogged={handleLogged}
                    addDeal={addDeal}
                    actualUser={actualUser}
                  />
                }
              />
            )}
            {logged && (
              <Route
                exact
                path="/client"
                element={
                  <Client
                    handleLogged={handleLogged}
                    dealToSearch={getDeals}
                    actualUser={actualUser}
                  />
                }
              />
            )}

            <Route
              exact
              path="/sellHistory"
              element={<SellHistory actualUser={actualUser} />}
            />

            <Route
              exact
              path="/buyHistory"
              element={<BuyHistory actualUser={actualUser} />}
            />

            <Route
              exact
              path="/register"
              element={
                <Register
                  users={users}
                  admins={admins}
                  handleRegister={handleRegister}
                />
              }
            />
          </Routes>
        </div>
      </Router>
    </div>
  );
}

export default App;
