import React from "react";
import { useState } from "react";
import "../Register.css";

const FileUpload = ({ actualUser }) => {
  const [cc, setCc] = useState();
  const [fuel, setFuel] = useState();
  const [hp, setHp] = useState();
  const [picture, setPicture] = useState();
  const [year, setYear] = useState();
  const [actualSellerId, setActualSellerId] = useState();
  const [selectedFile, setSelectedFile] = useState();
  const [isFilePicked, setIsFilePicked] = useState(false);

  const changeHandler = (e) => {
    setSelectedFile(e.target.files[0]);
    setIsFilePicked(true);
  };

  const handleOnSubmit = (e) => {
    e.preventDefault();
    setActualSellerId(actualUser);
    setPicture(selectedFile.name);

    console.log(actualUser);
    console.log(picture);
  };

  return (
    <div
      style={{
        position: "absolute",
        left: "25%",
        top: "50%",
        transform: "translate(-50%, -50%)",
        background: "grey",
      }}
      className="card col-md-6 offset-md-3 offset-md-3"
    >
      <form className="formm" onSubmit={handleOnSubmit}>
        <div className="input-grouppp">
          <label> CC </label>
          <input
            type="text"
            value={cc}
            placeholder="Enter engine capacity"
            onChange={(e) => setCc(e.target.value)}
          />
        </div>
        <div className="input-grouppp">
          <label>Fuel</label>
          <input
            type="text"
            value={fuel}
            placeholder="Enter fuel : GPL/BENZINA/DIESEL"
            onChange={(e) => setFuel(e.target.value)}
          />
        </div>
        <div className="input-grouppp">
          <label> HP </label>
          <input
            type="text"
            value={hp}
            placeholder="Enter hourse power"
            onChange={(e) => setHp(e.target.value)}
          />
        </div>

        <div className="input-grouppp">
          <label>Year</label>
          <input
            type="text"
            value={year}
            placeholder="Enter the manufacturing year"
            onChange={(e) => setYear(e.target.value)}
          />
        </div>

        <input type="file" name="file" onChange={changeHandler} />
        <button className="primaryyy buttonnn"> Add it</button>
      </form>
    </div>
  );
};

export default FileUpload;
