import "./App.css";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import Login from "./Components/Login";
import { useState, useEffect } from "react";
import { addUser, getUsers } from "./Services/UsersService";
import { getAdmins } from "./Services/AdminsService";
import Admin from "./Admin";
import User from "./User";
import Register from "./Components/Register";

function App() {
  const [logged, setLogged] = useState(false);
  const [users, setUsers] = useState([]);
  const [admins, setAdmins] = useState([]);

  useEffect(() => {
    getUsers().then((res) => {
      setUsers(res.data);
    });

    getAdmins().then((res) => {
      setAdmins(res.data);
    });
  }, []);

  const handleLogged = () => {
    logged ? setLogged(false) : setLogged(true);
  };

  const handleRegister = (newUser) => {
    addUser(newUser);
    setUsers([...users, newUser]);
  };

  return (
    <div>
      <Router>
        <div className="container">
          <Routes>
            <Route
              exact
              path="/"
              element={
                <Login
                  users={users}
                  admins={admins}
                  handleLogged={handleLogged}
                />
              }
            />
            <Route
              exact
              path="/login"
              element={
                <Login
                  users={users}
                  admins={admins}
                  handleLogged={handleLogged}
                />
              }
            />
            {logged && (
              <Route
                exact
                path="/admin"
                element={<Admin handleLogged={handleLogged} />}
              />
            )}
            {logged && (
              <Route
                exact
                path="/user"
                element={<User handleLogged={handleLogged} />}
              />
            )}
            <Route
              exact
              path="/register"
              element={
                <Register
                  users={users}
                  admins={admins}
                  handleRegister={handleRegister}
                />
              }
            />
          </Routes>
        </div>
      </Router>
    </div>
  );
}

export default App;
